mapper.select:
select *
from notice
where no = #{no}

select *
from freeboard
where no = #{no}

select *
from qaboard
where no = #{no}

mapper.update:
update freeboard
set visit=visit + 1
where no = #{no}

update freeboard
set good=good + 1
where no = #{no}

update freeboard
set good=good - 1
where no = #{no}

update qaboard
set good=good + 1
where no = #{no}

update qaboard
set good=good - 1
where no = #{no}

mapper.select:
select *
from qacomment
where qaboard_no = #{no}

select *
from freecomment
where freeboard_no = #{no}

mapper.update:
update freeboard
set report=report + 1
where no = #{no}

update qaboard
set report=report + 1
where no = #{no}

update dpboard
set report=report + 1
where no = #{no}

mapper.select:
select *
from alertboard
where alert_cate=#{alert_cate} and no=#{no}

select filename, freeboard_no
from freeboard_attach
where freeboard_no=#{no}

select filename, qaboard_no
from qaboard_attach
where qaboard_no=#{no}
